sections.css:
.container {
  background: $light-grey;
  &--no-background {
      background: none;
  }
}

@media (min-width: 576px) {
  .container {
    max-width: 768px;
  }
}

@media (min-width: 768px) {
  .container {
    max-width: 992px;
  }
}

@media (min-width: 992px) {
  .container {
    max-width: 1280px;
  }
}

p {
  @include default-font;
}

.summary {
  font-size: 1.2em;
  font-weight: 600;
}

.content {
  font-size: 1.1em;
  line-height: 1.5;
}

h1, h2, h3, h4, h5 {
  @include no-margin-padding;
  font-weight: 600;
}

h1 {
  font-size: 2em;
}

h2 {
  font-size: 1.5em;
}

h3 {
  font-size: 1.3em;
}

h4 {
  font-size: 1em;
}

a {
  @include teal-links;
}

.content p, .content ul, .content ol {
  max-width: 65ch;
  word-wrap: break-word;
}

.pointer {
  cursor: pointer;
}

.logo {
    &__image {
    max-width: 100%;
    height: 100%;
    padding: 0.5em 1em;
    }
    &__logo-link {
        padding: 0;
        text-align: center;
    }
  }

.primary-color {
    @include colour-combos;
}

.skip {
  left: -9000px;
  position: absolute;
  @include hover-state {
    left: 0;
  }
  &__link {
    color: $grey;
  }
}

@import "layout/_grid";
@import "layout/_header";
@import "layout/_footer";
@import "layout/_side-nav";
@import "layout/_off-canvas-nav";
@import "layout/_breadcrumb";
@import "layout/_jump-nav";
@import "layout/_skip-link";

.header-nav {
  background: $primary-color;
  position: relative;
  align-items: center;
  padding: 0.5em;
  margin: 0.5em 0;
 // @include media-breakpoint-down(md) {
 //   margin: 0;
 //  }

  &__logo-link {
    padding: 0;
    text-align: center;
  }

  &__logo-image {
    max-width: 100%;
    padding: 0 1.5em 0 0.5em;
    height: 3em;
  }

  &__items {
    display: flex;
    @media only screen and (max-width: 300px) {
      flex-direction: column;
    }
  }

  &__item {
    width: 100%;
 //   @include media-breakpoint-up(lg){
 //     border-left: 1px solid white;
 //   }
  }

  &__search-input {
    @include default-font;
    border: none;
    width: 100%;
    height: 3.1em;
    text-align: center;
  }

  &__link, &__search-button, &__hamburger-button {
    display: block;
    padding: 0 1em;
    line-height: 3.125rem;
    white-space: nowrap;
    text-decoration: none;
    color: $white;
    @include hover-state {
      @include teal-hover;
      outline: none;
      font-weight: 700;
    }
  }

  &__link {
    text-align: center;
    &--caret {
      padding-left: 0.5em;
    }
  }

  &__search-button, &__hamburger-button {
      border: none;
      background-color: transparent;
  }

  &__search-button {
    font-size: 1.1em;
    display: inline-block;
    width: 100%;
  }

  &__hamburger-button {
    font-size: 1.5em;
    width: 100%;
  }
}

.autocomplete {
  box-shadow: 3px 3px 0 0 rgba(0, 0, 0, 0.2);
  background: #fff;
  overflow: auto;
  @include button-radius;
  & .strong {
    font-weight: 400;
    color: $primary-color;
  }
  &__suggestion {
    padding: 0.625rem;
    overflow: hidden;
    border-bottom: 1px solid $light-grey;
  }
  &__selected {
    background: $dark-grey;
    color: $primary-color;
    @include hover-state {
      cursor: pointer;
    }
  }
}

.footer {
  padding-top: 1em;
  &--content-builder {
      margin-bottom: 3.2em;
 //     @include media-breakpoint-down(md) {
 //         margin-bottom: 4em;
 //     }
  }
  &__border-top {
    border-top: 0.3em solid $primary-color;
    width: 100%;
  }

  &__items-container {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: space-around;
    &--grey-bg {
      background: $light-grey;
    }
  }

  &__social {
    &-item {
      padding: 1em;
      width: auto;
 //     @include media-breakpoint-down(sm) {
 //       padding: 0.5em;
 //     }
    }
    &-icon {
      height: 3.125em;
      width: 3.125em;
      &--big {
        width: 13em;
      }
      &--facebook {
        background: #3b579d;
        border-radius: 50%;
        fill: #fff;
        padding: 0 0.4em 0 0.3em;
      }
    }
  }

  &__found-a-problem {
    background: $primary-color;
    padding: 1em 0;
    text-align: center;
    &-link {
      color: $white;
      margin: 0;
      @include underline-only-on-hover;
      @include hover-state {
        color: $white;
      }
    }
  }

  &__bottom-links {
    &-item {
      padding: 1em;
      width: auto;
      text-align: center;
//      @include media-breakpoint-down(sm) {
//        width: 100%;
//      }
    }
    &-link {
      color: $grey;
      @include underline-only-on-hover;
    }
  }
}

.side-nav {
  @include grey-panel;
  &__teal-button {
    margin-top: 0;
    font-weight: 600;
 //   @include media-breakpoint-up(md) {
 //     display: none;
 //   }
  }
  &__header-title {
    font-size: 1.125em;
    font-weight: 600;
    display: inline;
    padding: 0 0 .625em 0;
  }

  &__divider {
      border-bottom: 1px solid $white;
  }

  &__chevron {
      color: $secondary-color;
  }

  &__list {
    padding: 0;
    list-style: disc;
    &--current-section {
      padding: 0 1.5em;
    }
    &--no-margin {
      margin: 0;
    }
  }

  &__parent {
    @include no-bullets;
      padding: 0.5em 0;
  }

  &__current-section {
    background-color: $dark-grey;
    @include no-bullets;
    padding: 0.5em;
    &-title {
      font-weight: 600;
    }
  }

  &__sibling {
    margin: 0.5em 1em;
  }

  &__child {
    padding: 0.5em 0;
  }

  &__link {
    @include default-font;
    @include underline-only-on-hover;
  }

  &__share-item {
    display: inline-block;
    margin: 0 0.3em;
    padding-top: 0.75em;
    @include hover-state {
      transform: translateY(-3px);
    }
  }

  &__icon {
    display: inline-block;
    width: 2.7em;
    height: 2.7em;
    stroke-width: 0;
    border-radius: 50%;
    padding: 0.4em;
    fill: $white;

    &--twitter {
      background: #1DA1F2;
    }

    &--facebook {
      background: #3B579D;
    }

    &--whatsapp {
      background: #1AD13F;
    }

    &--email {
      background: light($grey);
    }
  }
}

.overlay {
   /*position: fixed; */
  display: none;
  width: 25%;
  height: 100%;
  background-color: rgba(0,0,0,0.5);
  z-index: 3;
  cursor: pointer;
  transition: fade 0.2s;
  &--on {
    display: block;
  }
}

.off-canvas-nav {
  /* overflow-x: hidden;
  visibility: hidden; */
  transition: 0.5s;
  height: 100%;
  width: 25%;
   /*position: fixed; */
  z-index: 3;
  top: 0;
  right: 0;
  padding-left: 1em;
  background-color: $primary-color;

 // @include media-breakpoint-down(sm) {
 //   padding-top: 15px;
 // }

  &--open {
    width: 17em;
    visibility: visible;
  }

  &__button {
    border: none;
    background-color: transparent;
    font-size: 1.1em;
    line-height: 0.5em;
    padding: 1em;
    float: right;
  }

  &__list {
    @include no-margin-padding;
    width: 12.5em;
  }

  &__item {
    height: 3.12em;
    vertical-align: middle;
    @include no-bullets;
    @include no-margin-padding;
  }

  &__link {
    line-height: 3em;
    padding-left: 1em;
    text-decoration: none;
    display: block;
  }

  &__link, &__button {
    color: $white;
    @include hover-state {
      @include teal-hover
    }
  }

  &__divider {
    border-bottom: 1px solid $white;
    margin-left: 0.5em;
    @include no-bullets;
  }
}

.breadcrumb {
    padding: 1.25em 0;
    width: 100%;
    &__home {
        text-decoration: none;
    }
    &__home-icon {
        color: $secondary-color;
        font-size: 2em;
    }
    &__item {
        @include teal-links;
        @include underline-only-on-hover;
        &:before {
          display: inline-block;
          margin: 0 1em;
          transform: rotate(15deg);
          border-left: 1px solid $grey;
          height: 1em;
          content: '';
        }
        &--current {
          color: $grey;
          @include hover-state {
            text-decoration: none;
          }
        }
    }
    &__divider {
        width: 100%;
        margin-top: 1em;
        border-bottom: 1px solid $grey;
    }
}

.jump-nav {
  display: none;
  position: fixed;
  left: 0;
  width: 100%;
  padding: 0.5em 0;
  z-index: 1000;
  background: rgba(255,255,255,0.9);
  border: $dark-grey 1px solid;
//  @include media-breakpoint-down(sm) {
//    background: $white;
//  }

  &__list {
    margin: 0;
    padding: 0 0.5em;
    display: flex;
  }

  &__item {
    display: block;
    padding: 0.5em 1em;
    margin: 0 1em;
    text-align: center;
    border-radius: 2em;
    color: $grey;
//    @include media-breakpoint-down(sm) {
//      width: 100%;
//    }

    &--active {
      background: $primary-color;
      color: $white;
      font-weight: 700;
      text-decoration: none;
      @include hover-state {
        color: $white;
      }
    }

//    &--mobile-hide {
//      @include media-breakpoint-down(sm) {
//        display: none;
//      }
//    }
  }
}

@import "components/_alerts";
@import "components/_multi-tab-slider";
@import "components/_wardmap";
@import "components/_banner";
@import "components/_galleries";
@import "components/_featured-images";
@import "components/_in-page-image";
@import "components/_attachments";
@import "components/_persona-parts";
@import "components/_quick-links";
@import "components/_bulletpoint";

.alert {
  background-color: $standard-alert;
  padding: 0.625em 1.25em;
  margin: 1em 0;
  &--warning {
    background-color: $warning-alert;
  }
  &--emergency {
    background-color: $emergency-alert;
    padding: 1em;
  }
  &__title {
    @include no-margin-padding;
    font-weight: 700;
    font-size: 1.1em;
    &--emergency {
      color: $white;
      font-size: 2em;
      padding-bottom: 0.5em;
    }
  }
  &__button {
    display: inline-block;
    padding: 1em;
    background: $white;
    color: $grey;
    font-weight: 700;
    @include hover-state {
      text-decoration: underline;
      color: $primary-color;
    }
  }
  &__text {
    @include no-margin-padding;
    &--emergency {
      color: $white;
    }
  }
  &__link {
    @include teal-links;
    font-weight: 700;
  }
  &__close {
    color: $grey;
    text-decoration: none;
    float: right;
    @include hover-state {
      color: light($grey);
    }
  }
}

.attachments {
  @include no-bullets;
  &__item {
    padding: 0.5em 0;
  }
  &__link {
    @include teal-links;
    @include underline-only-on-hover;
    text-align: center;
  }
  &__icon {
    color: $secondary-color;
    font-size: 3em;
  }
  &__text {
    font-weight: 600;
    text-align: left;
  }
}

.banner {
  overflow: hidden;
  padding: 1em 0;
  &__image {
    max-width: 100%;
//    @include media-breakpoint-up(md) {
//     height: 400px;
//      object-fit: cover;
//    }
  }
  &__caption {
    position: absolute;
    top: 0;
    right: 1em;
    padding: 1em;
//    @include media-breakpoint-up (lg) {
//     width: 50%;
//    }
//    @include media-breakpoint-down (md) {
//      position: relative;
//      top: 0;
//      right: 0;
//      padding: 0;
//    }
  }

  &__header {
    padding: 0.5em;
    margin: 0.5em 0;
    font-size: 1.5em;
    font-weight: 700;
    float: right;
//    @include media-breakpoint-down(md) {
//      padding: 0;
//      float: left;
//    }
  }
  &__text {
    padding: 0.5em;
    font-size: 1.3em;
    float: right;
    width: 75%;
//    @include media-breakpoint-down(md) {
//      padding: 0;
//      margin: 0;
//      float: left;
//      width: 100%;
//    }
  }
  &__button {
    position: absolute;
    bottom: 1em;
    right: 1.5em;
    padding: 1em;
    font-size: 1.2em;
    font-weight: 700;
    @include button-radius;
    @include underline-only-on-hover;
    @include button-shadow;
//    @include media-breakpoint-down(md) {
//      float: left;
//      position: inherit;
//    }
  }

//repeats the &__'s to comma seperate, so the colour combos don't have to be repeated
  &__header, &__text, &__button {
    @include colour-combos;
  }

//  &__header, &__text {
//    @include media-breakpoint-down(md) {
//      @include background-and-text($white, $black);
//    }
//  }

  &__pagination {
    text-align: center;
  }
  &__dot {
    font-size: 2em;
    color: $blue;
    padding: 0.5em;
    text-decoration: none;
    &--active {
      color: $secondary-color;
    }
  }
}

.bulletpoint {
  padding: 0.5em 0;
  max-width: 65ch;
  &__link {
    @include teal-links;
    @include underline-only-on-hover;
  }
  &__icon-align {
    text-align: center;
  }
  &__icon {
    color: $secondary-color;
    font-size: 3em;
  }
  &__text {
    font-weight: 600;
    text-align: left;
  }
}

.featured-image {
  width: 100%;
  height: 100%;
  max-height: 16.45em;
  object-fit: cover;
  &__container {
    width: 100%;
    height: 16.45em;
  }
  &--background {
    background-size: cover;
    background-position: center center;
  }
}

.gallery {
  padding: 1em 0;
  &--carousel {
    overflow: hidden;
    padding: 0.5em;
  }
  &-item {
    padding: 0.5em;
    &__image {
      padding: 1em;
      border: 1px $dark-grey solid;
      display: block;
      width: 100%;
      max-width: 13.75em;
      max-height: 13.7em;
      @include border-radius;
      &-link {
        display: block;
        transition: 0.2s ease-out 0.01s;
        width: 100%;
        max-height: 13.75em;
        max-width: 13.75em;
        @include border-radius;
        @include hover-state {
          box-shadow: 0 0 0.375em 0.0625em rgba(0, 0, 0, 0.8);
        }
      }
    }
    &__text-block {
      padding: 0.5em 0 0.5em 0;
    }
    &__title {
      &-link {
        font-size: 1.4em;
        color: $grey;
        @include hover-state {
          color: $primary-color;
        }
      }
    }
    &__summary {
      line-height: 1.5;
    }
  }

  &__button {
    @include button-shadow;
    @include button-radius;
    display: block;
    width: 75%;
    position: relative;
    padding: 1em 1.5em;
    margin-bottom: 1em;
    background-color: $secondary-color;
    color: $grey;
    text-align: center;
    text-decoration: none;
//    @include media-breakpoint-down(xs) {
//      width: 100%;
//    }
    @include hover-state {
      background-color: $orange-hover;
    }
  }
}

.lightbox {
  display: none;
  position: absolute;
  z-index: 2;
  top: 0;
//  @include media-breakpoint-up(md){
//    top: 12em;
//  }
  left: 50%;
  width: 100%;
  padding: 0 1em;
  transform: translate(-50%, -50%);

  &-container {
    display: none;
    height: 100%;
    width: 100%;
    z-index: 2;
  }

  &__overlay {
    background-color: rgba(0,0,0,0.8);
    z-index: 1;
    height: 100%;
    left: 0;
    position: fixed;
    top: 0;
    width: 100%;
    cursor: pointer;
  }
  &__close {
    color: $white;
    position: absolute;
    top: 0.5em;
    right: 0.5em;
    font-size: 2em;
    padding: 0.5em;
    cursor: pointer;
    text-decoration: none;
    z-index: 1;
    @include hover-state {
      color: $white;
      background-color: rgba(0, 0, 0, 0.8);
      text-decoration: none;
    }
  }

  &__content {
    position: relative;
    margin: 0;
    padding: 1em 0;
    width: 100%;
    overflow: hidden;
  }
  &__image {
    object-fit: cover;
//    @include media-breakpoint-down(md) {
//     @media only screen and (orientation: landscape) {
//       max-height: 15.5em;
//      }
//    }
  }

  &__caption {
    color: $white;
    display: inline-block;
  }

  &__numbers {
    color: $white;
    font-size: 0.75em;
    float: right;
  }

  &__prev, &__next {
    cursor: pointer;
    text-decoration: none;
    position: absolute;
    top: 50%;
    width: auto;
    padding: 0.5em;
    margin-top: -1em;
    color: $white;
    font-size: 2em;
    transition: 0.6s ease;
    border-radius: 0 3px 3px 0;
    user-select: none;
    -webkit-user-select: none;
    @include hover-state {
      color: $white;
      background-color: rgba(0, 0, 0, 0.8);
    }
  }
  &__next {
    right: 0;
    border-radius: 3px 0 0 3px;
  }
}


.carousel {
  &__pagination {
    text-align: center;
  }
  &__dot {
    font-size: 2em;
    color: $blue;
    padding: 0.5em;
    @include hover-state {
      cursor: pointer;
    }
    &--active {
      color: $secondary-color;
    }
  }
  &__arrow {
    font-size: 3em;
    color: $secondary-color;
    width: 50%;
    &--active {
      color: $blue;
    }
  }
  &__next {
    float: right;
  }
}

.in-page-image {
    display: inline-block;
    &--left {
      float: left;
      margin-right: 2em;
//      @include media-breakpoint-up(md){
 //       width: 50%;
 //     }
    }
    &--right {
      float: right;
      margin-left: 2em;
//      @include media-breakpoint-up(md){
//        width: 50%;
//      }
    }
    &--center {
      padding: 0.5em 0;
      width: 100%;
    }
//    @include media-breakpoint-down(md) {
//      float: none;
//      margin: 0;
//    }
    &__image {
      border: 2px solid $primary-color;
      margin-bottom: 0.5em;
      max-width: 100%;
      height: 100%;
      object-fit: contain;
      &--left {
        float: left;
        height: 16.45em;
      }
      &--right {
        float: right;
        height: 16.45em;
      }
      &--center {
        object-fit: contain;
        margin-bottom: 0;
        width: 100%;
        max-height: 16.45em;
      }
//      @include media-breakpoint-down(md) {
 //       float: none;
 //     }
    }
    &__icon {
      position: absolute;
      color: $white;
      display: block;
      padding: 1em;
      font-size: 1.2em;
      mix-blend-mode: difference;
      &--left {
        float: left;
      }
      &--right {
        float: right;
 //       @include media-breakpoint-up(lg){
 //         right: 1em;
 //       }
  //      @include media-breakpoint-down(md) {
 //         float: left;
 //       }
      }
    }
    &__text {
      border: 1px solid $light-grey;
      padding: 0.5em;
      margin: 0;
    }
}

.orange-button {
    position: relative;
    background-color: $secondary-color;
//    min-height: 12em;
    border-radius: 1em;
    flex: 1 1 10.7em;
    margin: 0.5em;
    overflow: hidden;
    @include button-shadow;

    @include media-breakpoint-down(sm) {
        min-width: 0;
    }

    &__description {
        width: 100%;
        padding-left: 5%;
        color: $black;
        overflow: hidden;
        @include button-radius-bottom;
    }

    &__link {
        @include grey-links;

        @include hover-state {
            text-decoration: none;
            outline: none;
            background: $orange-hover;
        }
    }

    &__title {
        display: block;
        text-align: center;
        line-height: 1.4em;
        padding: 0.3125em;
        position: relative;
        z-index: 2;
        background-color: $secondary-color;
        @include button-radius-top;
    }

    &__image {
        background-repeat: no-repeat;
        background-position: 50% 50%;
        background-size: cover;
        height: 100%;
        width: 100%;
        position: absolute;
        top: 1%;
        opacity: 1;
        transition: top .3s ease-out .3s;
        @include button-radius;
    }

    @include hover-state {
        background-color: $orange-hover;
        z-index: 20;
        height: auto;

        .orange-button__image {
            top: 100%;
        }
    }
}

.tab {
	&__header {
		position: relative;
		display: block;
		margin-top: 0.5em;
		height: 3em;
		border-radius: 1.25em;
		border: 0.0625em solid $dark-grey;
		transition: display 1s;
		@include hover-state {
			outline: none;
			.tab__header-arrow {
