  }
  &__title {
    @include no-margin-padding;
    font-weight: 700;
    font-size: 1.1em;
    &--emergency {
      color: $white;
      font-size: 2em;
      padding-bottom: 0.5em;
    }
  }
  &__button {
    display: inline-block;
    padding: 1em;
    background: $white;
    color: $grey;
    font-weight: 700;
    @include hover-state {
      text-decoration: underline;
      color: $primary-color;
    }
  }
  &__text {
    @include no-margin-padding;
