    height: 16.45em;
  }
  &--background {
    background-size: cover;
    background-position: center center;
  }
}

.gallery {
  padding: 1em 0;
  &--carousel {
    overflow: hidden;
    padding: 0.5em;
  }
  &-item {
    padding: 0.5em;
    &__image {
      padding: 1em;
      border: 1px $dark-grey solid;
      display: block;
      width: 100%;
      max-width: 13.75em;
      max-height: 13.7em;
      @include border-radius;
      &-link {
        display: block;
        transition: 0.2s ease-out 0.01s;
        width: 100%;
        max-height: 13.75em;
        max-width: 13.75em;
        @include border-radius;
        @include hover-state {
          box-shadow: 0 0 0.375em 0.0625em rgba(0, 0, 0, 0.8);
        }
      }
    }
    &__text-block {
      padding: 0.5em 0 0.5em 0;
    }
    &__title {
      &-link {
        font-size: 1.4em;
        color: $grey;
        @include hover-state {
          color: $primary-color;
        }
      }
    }
    &__summary {
      line-height: 1.5;
    }
  }

  &__button {
    @include button-shadow;
    @include button-radius;
    display: block;
    width: 75%;
    position: relative;
    padding: 1em 1.5em;
    margin-bottom: 1em;
    background-color: $secondary-color;
    color: $grey;
    text-align: center;
    text-decoration: none;
//    @include media-breakpoint-down(xs) {
//      width: 100%;
//    }
    @include hover-state {
      background-color: $orange-hover;
    }
  }
}

.lightbox {
  display: none;
  position: absolute;
  z-index: 2;
  top: 0;
//  @include media-breakpoint-up(md){
//    top: 12em;
//  }
  left: 50%;
  width: 100%;
  padding: 0 1em;
  transform: translate(-50%, -50%);

  &-container {
    display: none;
    height: 100%;
    width: 100%;
    z-index: 2;
  }

  &__overlay {
    background-color: rgba(0,0,0,0.8);
    z-index: 1;
    height: 100%;
    left: 0;
    position: fixed;
    top: 0;
    width: 100%;
    cursor: pointer;
  }
  &__close {
    color: $white;
    position: absolute;
    top: 0.5em;
    right: 0.5em;
    font-size: 2em;
    padding: 0.5em;
    cursor: pointer;
    text-decoration: none;
    z-index: 1;
    @include hover-state {
      color: $white;
      background-color: rgba(0, 0, 0, 0.8);
      text-decoration: none;
    }
  }

  &__content {
    position: relative;
    margin: 0;
    padding: 1em 0;
    width: 100%;
    overflow: hidden;
  }
  &__image {
    object-fit: cover;
//    @include media-breakpoint-down(md) {
//     @media only screen and (orientation: landscape) {
//       max-height: 15.5em;
//      }
//    }
  }

  &__caption {
    color: $white;
    display: inline-block;
  }

  &__numbers {
    color: $white;
    font-size: 0.75em;
    float: right;
  }

  &__prev, &__next {
    cursor: pointer;
    text-decoration: none;
    position: absolute;
    top: 50%;
    width: auto;
    padding: 0.5em;
    margin-top: -1em;
    color: $white;
    font-size: 2em;
    transition: 0.6s ease;
    border-radius: 0 3px 3px 0;
    user-select: none;
    -webkit-user-select: none;
    @include hover-state {
      color: $white;
      background-color: rgba(0, 0, 0, 0.8);
    }
  }
  &__next {
    right: 0;
    border-radius: 3px 0 0 3px;
  }
}


.carousel {
  &__pagination {
    text-align: center;
  }
  &__dot {
    font-size: 2em;
    color: $blue;
    padding: 0.5em;
    @include hover-state {
      cursor: pointer;
    }
    &--active {
      color: $secondary-color;
    }
  }
  &__arrow {
    font-size: 3em;
    color: $secondary-color;
    width: 50%;
    &--active {
      color: $blue;
    }
  }
  &__next {
    float: right;
  }
}

.in-page-image {
    display: inline-block;
    &--left {
      float: left;
      margin-right: 2em;
//      @include media-breakpoint-up(md){
 //       width: 50%;
 //     }
    }
    &--right {
      float: right;
      margin-left: 2em;
//      @include media-breakpoint-up(md){
//        width: 50%;
//      }
    }
    &--center {
      padding: 0.5em 0;
      width: 100%;
    }
//    @include media-breakpoint-down(md) {
//      float: none;
//      margin: 0;
//    }
    &__image {
      border: 2px solid $primary-color;
      margin-bottom: 0.5em;
      max-width: 100%;
      height: 100%;
      object-fit: contain;
      &--left {
        float: left;
        height: 16.45em;
      }
      &--right {
        float: right;
        height: 16.45em;
      }
      &--center {
        object-fit: contain;
        margin-bottom: 0;
        width: 100%;
        max-height: 16.45em;
      }
//      @include media-breakpoint-down(md) {
 //       float: none;
 //     }
    }
    &__icon {
      position: absolute;
      color: $white;
      display: block;
      padding: 1em;
      font-size: 1.2em;
      mix-blend-mode: difference;
      &--left {
        float: left;
      }
      &--right {
        float: right;
 //       @include media-breakpoint-up(lg){
 //         right: 1em;
 //       }
  //      @include media-breakpoint-down(md) {
 //         float: left;
 //       }
      }
    }
    &__text {
      border: 1px solid $light-grey;
      padding: 0.5em;
      margin: 0;
    }
}

.orange-button {
    position: relative;
    background-color: $secondary-color;
//    min-height: 12em;
    border-radius: 1em;
    flex: 1 1 10.7em;
    margin: 0.5em;
    overflow: hidden;
    @include button-shadow;

    @include media-breakpoint-down(sm) {
        min-width: 0;
    }

    &__description {
        width: 100%;
        padding-left: 5%;
        color: $black;
        overflow: hidden;
        @include button-radius-bottom;
    }

    &__link {
        @include grey-links;

        @include hover-state {
            text-decoration: none;
            outline: none;
            background: $orange-hover;
        }
    }

    &__title {
        display: block;
        text-align: center;
        line-height: 1.4em;
        padding: 0.3125em;
        position: relative;
        z-index: 2;
        background-color: $secondary-color;
        @include button-radius-top;
    }

    &__image {
        background-repeat: no-repeat;
        background-position: 50% 50%;
        background-size: cover;
        height: 100%;
        width: 100%;
        position: absolute;
        top: 1%;
        opacity: 1;
        transition: top .3s ease-out .3s;
        @include button-radius;
    }

    @include hover-state {
        background-color: $orange-hover;
        z-index: 20;
        height: auto;

        .orange-button__image {
            top: 100%;
        }
    }
}

.tab {
	&__header {
		position: relative;
		display: block;
		margin-top: 0.5em;
		height: 3em;
		border-radius: 1.25em;
		border: 0.0625em solid $dark-grey;
		transition: display 1s;
		@include hover-state {
			outline: none;
			.tab__header-arrow {
				background: $teal-hover;
			}
		}

		&.is-collapsed {
			@include media-breakpoint-up(lg){
				height: 15em;
				background-color: transparent;
				float: left;
			}
			+ .row {
				visibility: hidden;
				height: 0;
				opacity: 0;
			}
		}

	&-link {
		@include underline-only-on-hover;
		@include hover-state {
			outline: none;
		}
	}
	&-arrow {
		height: 100%;
		font-size: 1.25em;
		padding: 0.5em 1em;
		float: right;
		background-color: $secondary-color;
		color: $white;
		border-radius: 0 1em 1em 0;
		&.is-expanded {
			transform: rotate(180deg);
			border-radius: 1em 0 0 1em;
		}
			&.is-collapsed {
				@include media-breakpoint-up(lg) {
					height: 2.3em;
					border-radius: 2em;
				}
			}

		&-icon {
			transition: 0.3s;
			&.is-collapsed {
				transform: rotate(90deg);
			}
		}
	}
	&-text {
		color: $grey;
		font-size: 1.4em;
		line-height: 2em;
		font-weight: 400;
		padding-left: 0.5em;
		&.is-collapsed {
			@include media-breakpoint-up(lg) {
				width: 15em;
				animation: rotateDownRight 0.3s both;
				position: absolute;
			}
		}
	}
	&__content {
		opacity: 1;
		width: 100%;
    	padding-top: 0.5em;
    	display: flex;
    	flex-wrap: wrap;
		@include media-breakpoint-up(lg){
			flex-direction: row;
		}
		&--orange-buttons {
	    display: grid;
	    grid-template-columns: repeat(1, 1fr);
			@include media-breakpoint-up(sm){
				grid-template-columns: repeat(3, 1fr);
			}
		}
		&--blue-buttons {
			flex-direction: column;
		}
		&--force-wrap {
			@include media-breakpoint-up(md) {
				max-height: 30em;
			}
		}
		&--row {
			flex-direction: row;
		}
		&--news, &--newsletter {
			flex-direction: inherit;
			max-height: 100%;
		}
	}

	&__content-header {
		font-size: 1.5em;
		font-weight: 400;
		&-link {
			color: $grey;
			@include underline-only-on-hover
		}
	}
}};

@keyframes rotateDownRight {
	0% {
		-webkit-transform-origin: left bottom;
		-ms-transform-origin: left bottom;
		transform-origin: left bottom;
		-webkit-transform: rotate(0);
		-ms-transform: rotate(0);
		transform: rotate(0)
	}

	100% {
		-webkit-transform-origin: left bottom;
		-ms-transform-origin: left bottom;
		transform-origin: left bottom;
		-webkit-transform: rotate(90deg);
		-ms-transform: rotate(90deg);
		transform: rotate(90deg)
	}
}

@-webkit-keyframes rotateDownRight {
	0% {
		-webkit-transform-origin: left bottom;
		transform-origin: left bottom;
		-webkit-transform: rotate(0);
		transform: rotate(0)
	}

	100% {
		-webkit-transform-origin: left bottom;
		transform-origin: left bottom;
		-webkit-transform: rotate(90deg);
		transform: rotate(90deg)
	}
}

.latest-news {
	margin: 0.5em 1em;
	padding: 0;
	&__item {
		line-height: 1.5em;
		padding: 0.2em;
	}
	&__link {
		@include teal-links;
		@include underline-only-on-hover
	}
	&__your-leicester {
		width: 100%;
	}
}

#MTS [class*='col-'] {
	@include media-breakpoint-up(lg) {
		transition: all 0.3s ease-in-out;
	}
 }

 .is-expanded + .row .tab__content--orange-buttons {
	@include media-breakpoint-up(sm){
		grid-template-columns: repeat(3, 1fr);
	}
	@include media-breakpoint-up(md) {
		grid-template-columns: repeat(4, 1fr);
	}
	@include media-breakpoint-up(lg){
		grid-template-columns: repeat(5, 1fr);
	}
 }

.tab {
	&__header {
		position: relative;
		display: block;
		margin-top: 0.5em;
		height: 3em;
		border-radius: 1.25em;
		border: 0.0625em solid $dark-grey;
		transition: display 1s;
		@include hover-state {
			outline: none;
			.tab__header-arrow {
				background: $teal-hover;
			}
		}

		&.is-collapsed {
			@include media-breakpoint-up(lg){
				height: 15em;
				background-color: transparent;
				float: left;
			}
			+ .row {
				visibility: hidden;
				height: 0;
				opacity: 0;
			}
		}

	&-link {
		@include underline-only-on-hover;
		@include hover-state {
			outline: none;
		}
	}
	&-arrow {
		height: 100%;
		font-size: 1.25em;
		padding: 0.5em 1em;
		float: right;
		background-color: $secondary-color;
		color: $white;
		border-radius: 0 1em 1em 0;
		&.is-expanded {
			transform: rotate(180deg);
			border-radius: 1em 0 0 1em;
		}
			&.is-collapsed {
				@include media-breakpoint-up(lg) {
					height: 2.3em;
					border-radius: 2em;
				}
			}

		&-icon {
			transition: 0.3s;
			&.is-collapsed {
				transform: rotate(90deg);
			}
		}
	}
	&-text {
		color: $grey;
		font-size: 1.4em;
		line-height: 2em;
		font-weight: 400;
		padding-left: 0.5em;
		&.is-collapsed {
			@include media-breakpoint-up(lg) {
				width: 15em;
				animation: rotateDownRight 0.3s both;
				position: absolute;
			}
		}
	}
	&__content {
		opacity: 1;
		width: 100%;
    padding-top: 0.5em;
    display: flex;
    flex-wrap: wrap;
		@include media-breakpoint-up(lg){
			flex-direction: row;
		}
		&--orange-buttons {
	    display: grid;
	    grid-template-columns: repeat(1, 1fr);
			@include media-breakpoint-up(sm){
				grid-template-columns: repeat(3, 1fr);
			}
		}
		&--blue-buttons {
			flex-direction: column;
		}
		&--force-wrap {
			@include media-breakpoint-up(md) {
				max-height: 30em;
			}
		}
		&--row {
			flex-direction: row;
		}
		&--news, &--newsletter {
			flex-direction: inherit;
			max-height: 100%;
		}
	}

	&__content-header {
		font-size: 1.5em;
		font-weight: 400;
		&-link {
			color: $grey;
			@include underline-only-on-hover
		}
	}
}};

@keyframes rotateDownRight {
	0% {
		-webkit-transform-origin: left bottom;
		-ms-transform-origin: left bottom;
		transform-origin: left bottom;
		-webkit-transform: rotate(0);
		-ms-transform: rotate(0);
		transform: rotate(0)
	}

	100% {
		-webkit-transform-origin: left bottom;
		-ms-transform-origin: left bottom;
		transform-origin: left bottom;
		-webkit-transform: rotate(90deg);
		-ms-transform: rotate(90deg);
		transform: rotate(90deg)
	}
}

@-webkit-keyframes rotateDownRight {
	0% {
		-webkit-transform-origin: left bottom;
		transform-origin: left bottom;
		-webkit-transform: rotate(0);
		transform: rotate(0)
	}

	100% {
		-webkit-transform-origin: left bottom;
		transform-origin: left bottom;
		-webkit-transform: rotate(90deg);
		transform: rotate(90deg)
	}
}

.latest-news {
	margin: 0.5em 1em;
	padding: 0;
	&__item {
		line-height: 1.5em;
		padding: 0.2em;
	}
	&__link {
		@include teal-links;
		@include underline-only-on-hover
	}
	&__your-leicester {
		width: 100%;
	}
}

#MTS [class*='col-'] {
	@include media-breakpoint-up(lg) {
		transition: all 0.3s ease-in-out;
	}
 }

 .is-expanded + .row .tab__content--orange-buttons {
	@include media-breakpoint-up(sm){
		grid-template-columns: repeat(3, 1fr);
	}
	@include media-breakpoint-up(md) {
		grid-template-columns: repeat(4, 1fr);
	}
	@include media-breakpoint-up(lg){
		grid-template-columns: repeat(5, 1fr);
	}
 }

 .blue-button {
    position: relative;
    background-color: $tertiary-color;
    width: 100%;
    min-height: 5em;
    margin: 0.5em 0;
    transition: background-color 300ms ease-out 0.01s;
    @include button-radius;
    @include button-shadow;

    @include hover-state {
        background-color: $secondary-color;
    }

    &__link {
        @include grey-links;

        @include hover-state {
            outline: none;
        }
    }

    &__title {
        display: block;
        line-height: 1.4em;
        padding: 0.625em 0.625em 0.625em 33%;
    }

    &__image {
        background-size: cover;
        position: absolute;
        height: 100%;
        width: 30%;
        max-width: 9.37em;
        @include button-radius-left;

       @include media-breakpoint-down(sm) {
          background-repeat: no-repeat;
          background-size: auto;
        }
    }
}

.col-xl-6 .blue-button {
   @include media-breakpoint-up(md) {
       max-width: 30%;
    }

   @include media-breakpoint-up(xl) {
        max-width: 49%;
       margin: 0.5em;
    }
 }

 .tab__content--row .blue-button {
   @include media-breakpoint-down(sm) {
       width: 47%;
       margin: 0.5em;
   }

   @include media-breakpoint-down(xs) {
       width: 100%;
   }
}

.is-expanded + .row .tab__content--row .blue-button {
    margin: 0.5em;

   @include media-breakpoint-up(sm) {
       width: 47%;
   }

    @include media-breakpoint-up(lg) {
        width: 30%;
    }
}

.tab__content--row .blue-button + .sr-only {
    width: 0;
}

.persona-part {
  margin-top: 2em;
  margin-left: 1.5em;
  &__title {
    font-size: 1.3em;
    font-weight: 600;
    position: relative;
  }
  &__image {
    border-radius: 50%;
    border: 1px solid $secondary-color;
    height: 9.3em;
    float: left;
    margin-top: -1.5em;
    margin-left: -2em;
  }
  &__text {
    border: 1px solid $secondary-color;
    padding: 1em 1em 1em 7.5em;
    bottom: 0;
  }
}

.qa-block {
    border: 1px solid $light-grey;
    &__question {
      &-block {
        @include button-reset;
        display: flex;
        padding: 1em;
        width: 100%;
        @include hover-state {
          background-color: $light-grey;
        }
      }
      &-text {
        text-align: left;
      }
    }
    &__arrow {
      padding: 0 .5em;
      color: $secondary-color;
      transition: all .5s linear;
      -moz-transition: all .5s linear;
      -webkit-transition: all .5s linear;
      &--up {
        -ms-transform: rotate(180deg);
        -moz-transform: rotate(180deg);
        -webkit-transform: rotate(180deg);
        transform: rotate(180deg)
      }
    }
    &__answer {
      &-block {
        padding: 0 1em
      }
    }
}

.quick-links {
  @include no-bullets;
  &__item {
    padding: 0.5em 0;
  }
  &__link {
    @include teal-links;
    @include underline-only-on-hover;
    text-align: center;
  }
  &__icon {
    color: $secondary-color;
    font-size: 3em;
  }
  &__text {
    font-weight: 600;
    text-align: left;
  }
}

.ward-map {
  @include grey-panel;
  width: 100%;

  &__input {
    @include default-font;
    padding: 0 1em;
    height: 2.2em;
    width: 100%;
    font-size: 1em;
    line-height: 1em;
    text-align: left;
    border: 1px solid $white;
    @include button-radius-left;
  }

  &__button {
    @include button-reset;
    background-color: $primary-color;
    height: 2.2em;
    width: 100%;
    @include button-radius-right;
    @include hover-state {
      @include teal-hover;
    }
  }

  &__icon {
    text-align: center;
    line-height: 2.125em;
    font-size: 1em;
    color: $white;
  }

  &__results {
    line-height: 1.6;
    margin: 1.25em 0;
  }

  &__result-title {
    line-height: 1;
    margin: 1em 0;
  }

  &__link {
    @include underline-only-on-hover;
    @include teal-links;
    margin-bottom: .75em;
  }

  &__map {
    height: 100%;
    width: 100%;
  }

  &__map-item {
    cursor: pointer;
    -webkit-transition: fill .2s ease-in .01s;
    -moz-transition: fill .2s ease-in .01s;
    transition: fill .2s ease-in .01s;
    fill: #64802b;
    stroke-width: 1px;
    stroke-linecap: round;
    stroke-linejoin: round;
    stroke: $white;

    @include hover-state {
      fill: #3ea9cd;
      stroke-width: 3px;
      stroke: $black;
      outline: none;
    }
  }

  &__waiting-msg {
    padding: 0em 1.25em;
    background: url(/images/waiting.gif) 0 50% no-repeat;
  }
}

@import "pages/_atoz";
@import "pages/_cabinet";
@import "pages/_search-results";

.atoz {
  &__panel {
    @include grey-panel;
    display: table;
    &--hidden {
      display: none;
    }
  }

  &__letters {
    max-width: 100% !important; //important to override the max-width: 65ch in typography
    list-style: none;
    @include no-margin-padding;
  }

  &__letter {
    float: left;
    padding: 0.5em;
    @include media-breakpoint-down(sm) {
      padding: 0.4em;
    }
    @include media-breakpoint-down(xs) {
      padding: 0.3em;
    }
  }

  &__letter-link {
    display: table;
    height: auto;
    padding: 1.25em;
    width: 4em;
    text-decoration: none;
    text-align: center;
    color: $grey;
    background-color: $secondary-color;
    @include border-radius;
    @include hover-state {
      color: $white;
      background: $primary-color;
    }
    &--current {
      color: $white;
      background: $primary-color;
    }
  }

  &__results {
    max-width: 100% !important; //important to override the max-width: 65ch in typography
    columns: 3;
    display: inline-block;
    @include no-bullets;
    @include media-breakpoint-down(md) {
      columns: 2;
    }
    @include media-breakpoint-down(xs) {
      columns: 1;
    }
  }

  &__results-link {
    display: inline-block;
    padding: 1em;
    @include teal-links;
    @include media-breakpoint-down(md) {
      padding: 1.2em;
      width: 100%;
    }
  }
}

.search-results {
  padding: 1em 0;
  line-height: 1.5em;
  max-width: 65ch;
  &__bold-text {
    font-weight: 600;
  }
  &__input {
    @include default-font;
    padding: 0 0.8em 0 0.9em;
    height: 2.1em;
    width: 100%;
    border: 1px solid $primary-color;
    @include button-radius-left;
  }

  &__button {
    @include button-reset;
    background-color: $primary-color;
    width: 100%;
    height: 2.125em;
    @include button-radius-right;
  }

  &__icon {
    text-align: center;
    font-size: 1em;
    color: $white;
  }

  &__title {
    @include teal-links;
    font-size: 1.2em;
    font-weight: 600;
  }

  &__url {
    color: $primary-color
  }

  &__pagination {
    &-list {
    @include no-bullets;
    display: flex;
    justify-content: space-between;
    max-width: 100% !important; //important to override the max-width: 65ch in typography
  }
    &-item {
      display: inline-block;
      width: 10%;
      text-align: center;

      &--next, &--prev {
        color: $secondary-color;
        font-size: 2em;
        @include hover-state {
          cursor: pointer;
          color: $primary-color;
          text-decoration: underline;
        }
      }
    }
    &-link {
      @include teal-links;
      font-size: 1.2em;
      vertical-align: middle;
      &--active {
        color: $grey;
        font-weight: 600;
      }
    }
  }
}

